import React, {useEffect, useState} from 'react';
import Style from "./Questions.module.css";
import { Button } from '@mui/material';

const QuestionsModal = () => {
    const [counter, setCounter] = useState(1)
    const [score, setScore] = useState(0);
    const [isCorrectAnswer, setIsCorrectAnswer] = useState(false);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showFinalResults, setShowFinalResults] = useState(false);

    const questions = [
        {
            questionTitle: "What is the tallest mountain in the world?",
            options : [
                {id: 1, title: "K2", isCorrect: false},
                {id: 2, title: "Mt. Everest", isCorrect: true},
                {id: 3, title: "Kangchenjunga", isCorrect: false},
                {id: 4, title: "Pilot Butte", isCorrect: false},
            ],
        },
        {
            questionTitle: "What is the longest river in the world?",
            options : [
                {id: 1, title: "Amazon", isCorrect: true},
                {id: 2, title: "Nile", isCorrect: false},
                {id: 3, title: "James", isCorrect: false},
                {id: 4, title: "Mississippi", isCorrect: false},
            ],
        },
        {
            questionTitle: "Which country is also called The Netherlands?",
            options : [
                {id: 1, title: "Norway", isCorrect: false},
                {id: 2, title: "Sweden", isCorrect: false},
                {id: 3, title: "Holland", isCorrect: true},
                {id: 4, title: "Canada", isCorrect: false},
            ],
        },
        {
            questionTitle: "How many time zones does Russia have?",
            options : [
                {id: 1, title: "2", isCorrect: false},
                {id: 2, title: "7", isCorrect: false},
                {id: 3, title: "8", isCorrect: false},
                {id: 4, title: "11", isCorrect: true},
            ],
        },
        {
            questionTitle: "What type of leaf is on the Canadian flag?",
            options : [
                {id: 1, title: "Oak", isCorrect: false},
                {id: 2, title: "Maple", isCorrect: true},
                {id: 3, title: "Chesnut", isCorrect: false},
                {id: 4, title: "Arrowhead", isCorrect: false},
            ],
        },
        {
            questionTitle: "What country is known to have the best quality tap water?",
            options : [
                {id: 1, title: "USA", isCorrect: false},
                {id: 2, title: "Canada", isCorrect: false},
                {id: 3, title: "Switzerland", isCorrect: true},
                {id: 4, title: "Finland", isCorrect: false},
            ],
        },
        {
            questionTitle: "What is the capital of America?",
            options: [
                { id: 0, text: "New York City", isCorrect: false },
                { id: 1, text: "Boston", isCorrect: false },
                { id: 2, text: "Santa Fe", isCorrect: false },
                { id: 3, text: "Washington DC", isCorrect: true },
            ],
        },
        {
            questionTitle: "What year was the Constitution of America written?",
            options: [
                { id: 0, text: "1787", isCorrect: true },
                { id: 1, text: "1776", isCorrect: false },
                { id: 2, text: "1774", isCorrect: false },
                { id: 3, text: "1826", isCorrect: false },
            ],
        },
        {
            questionTitle: "Who was the second president of the US?",
            options: [
                { id: 0, text: "John Adams", isCorrect: true },
                { id: 1, text: "Paul Revere", isCorrect: false },
                { id: 2, text: "Thomas Jefferson", isCorrect: false },
                { id: 3, text: "Benjamin Franklin", isCorrect: false },
            ],
        },
        {
            questionTitle: "What is the largest state in the US?",
            options: [
                { id: 0, text: "California", isCorrect: false },
                { id: 1, text: "Alaska", isCorrect: true },
                { id: 2, text: "Texas", isCorrect: false },
                { id: 3, text: "Montana", isCorrect: false },
            ],
        },
        {
            questionTitle: "Which of the following countries DO NOT border the US?",
            options: [
                { id: 0, text: "Canada", isCorrect: false },
                { id: 1, text: "Russia", isCorrect: true },
                { id: 2, text: "Cuba", isCorrect: true },
                { id: 3, text: "Mexico", isCorrect: false },
            ],
        },
    ];

    const handleAnswer = (e:any) => {
        console.log('test', e);
        if (e === true) {
            setCounter(counter + 1);
            setScore(score + 1);
        } else {
            console.log('wrong')
        }

        if (currentQuestion + 1 < questions.length ) {
            setCurrentQuestion(currentQuestion + 1);
        } else {
            setShowFinalResults(true);
            console.log('score', score)
        }
    }

    const handleRestart = () => {
        setCounter(1);
        setScore(0);
        setShowFinalResults(false);
        setCurrentQuestion(0);
    }


    return (
        <div className={Style.modal}>
            <div className={Style.modal_inner}>
                <h3 className={Style.modal__inner__title}>Question {counter}</h3>
                <h5 className={Style.modal__inner__subtitle}> {currentQuestion + 1} out of {questions.length}</h5>
                <h4 className={Style.modal__inner__subtitle}>{questions[currentQuestion].questionTitle}</h4>
                <div className={Style.modal__inner__btn_container}>
                    <div className={Style.modal__inner__btn_container}>
                        {showFinalResults 
                        ? 
                            <>
                                <h1>Quiz Results</h1>
                                <h5>{score} correct out of {questions.length}</h5>
                                <Button
                                    variant="outlined"
                                    color={isCorrectAnswer ? "primary" : "secondary"}
                                    data-value=''
                                    onClick={() => handleRestart()}
                                >
                                    Restart Game
                                </Button>
                            </>
                        : 
                            <div className={Style.button_row}>
                                {questions[currentQuestion].options.map((_option, i) => {
                                    return (
                                        <div key={_option.id} className={Style.modal__inner__btn_container__item}>
                                            <Button
                                                variant="outlined"
                                                color={isCorrectAnswer ? "primary" : "secondary"}
                                                data-value=''
                                                onClick={() => handleAnswer(_option.isCorrect)}
                                            >
                                                {_option.title}
                                            </Button>
                                        </div>
                                    )
                                })}
                            </div>
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default QuestionsModal
