  return (
    <div className="App">
      <div className="main-bg">
        {startModal 
        ? <StartModal 
        />
        : <div className='error'>We're sorry, there has been an error</div>
        }

        {questionsModal
        ? <QuestionsModal />
        : null
        }
      </div>
    </div>
  );
}

export default App;
